import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";

const TodoItem = ({ list, getData }) => {
  const [isModify, setIsModify] = useState(false);
  const [modifyText, setModifyText] = useState("");

  useEffect(() => {
    getData();
  }, []);

  const modifyItem = async (e) => {
    setIsModify(!isModify);
    e.preventDefault();
    await axios.put(`http://localhost:4001/todo/${list.id}`, {
      content: modifyText
    });
    getData();
  };

  const modifyContent = (e) => {
    setModifyText(e.target.value);
  };

  const deleteItem = async (id) => {
    await axios.delete(`http://localhost:4001/todo/${id}`);
    getData();
  };

  return (
    <Item>
      <Task onSubmit={modifyItem}>
        {isModify ? (
          <input name="text" onChange={modifyContent} />
        ) : (
          <div>{list?.content}</div>
        )}
      </Task>
      <div>{list?.done}</div>
      <Btn>
        <div>{list?.updatedAt}</div>
        <ModifyBtn
          onClick={(e) => {
            e.target.value = list.content;
            setIsModify(!isModify);
          }}
          type="submit"
          value="수정"
        />
        <DeleteBtn onClick={() => deleteItem(list.id)}>삭제</DeleteBtn>
      </Btn>
    </Item>
  );
};

const Item = styled.div`
  display: flex;
  margin: 5rem;
  justify-content: space-between;
  align-items: center;
  > div {
    margin: 1rem;
  }

  transition: 500ms;
`;

const Task = styled.form`
  font-size: 2rem;
  > input {
    font-size: 2rem;
    border-radius: 10px;
    border: none;
  }
`;

const Btn = styled.div``;

const ModifyBtn = styled.input`
  appearance: none;
  background-color: transparent;
  border: 2px solid #1a1a1a;
  border-radius: 15px;
  box-sizing: border-box;
  color: #3b3b3b;
  cursor: pointer;
  display: inline-block;
  font-size: 16px;
  font-weight: 600;
  line-height: normal;
  margin: 0.5rem;
  min-height: 60px;
  outline: none;
  padding: 16px 24px;
  text-align: center;
  text-decoration: none;
  transition: all 300ms cubic-bezier(0.23, 1, 0.32, 1);
  user-select: none;
  -webkit-user-select: none;
  touch-action: manipulation;
  width: 100%;
  will-change: transform;

  :hover {
    color: #fff;
    background-color: #1a1a1a;
    box-shadow: rgba(0, 0, 0, 0.25) 0 8px 15px;
    transform: translateY(-2px);
  }

  :active {
    box-shadow: none;
    transform: translateY(0);
  }
`;

const DeleteBtn = styled.button`
  appearance: none;
  background-color: transparent;
  border: 2px solid #1a1a1a;
  border-radius: 15px;
  box-sizing: border-box;
  color: #3b3b3b;
  cursor: pointer;
  display: inline-block;
  font-family: Roobert, -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica,
    Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
  font-size: 16px;
  font-weight: 600;
  line-height: normal;
  margin: 0;
  min-height: 60px;
  min-width: 0;
  outline: none;
  padding: 16px 24px;
  text-align: center;
  text-decoration: none;
  transition: all 300ms cubic-bezier(0.23, 1, 0.32, 1);
  user-select: none;
  -webkit-user-select: none;
  touch-action: manipulation;
  width: 100%;
  will-change: transform;

  :disabled {
    pointer-events: none;
  }

  :hover {
    color: #fff;
    background-color: #1a1a1a;
    box-shadow: rgba(0, 0, 0, 0.25) 0 8px 15px;
    transform: translateY(-2px);
  }

  :active {
    box-shadow: none;
    transform: translateY(0);
  }
`;
export default TodoItem;
