import React, { useEffect, useState } from "react";
import axios from "axios";

const TodoItem = ({ list, getData }) => {
  const [isModify, setIsModify] = useState(false);
  useEffect(() => {
    getData();
  }, []);

  const modifyItem = async (e, id) => {
    setIsModify(!isModify);
    const content = e.target.text.value;
    await axios.put(`http://localhost:4001/todo/${id}`, {
      content
    });
    getData();
  };

  const deleteItem = async (id) => {
    await axios.delete(`http://localhost:4001/todo/${id}`);
    getData();
  };

  return (
    <div>
      <div>할 일</div>
      {isModify ? <input name="text" /> : <div>{list?.content}</div>}
      <div>날짜</div>
      <div>{list?.createdAt}</div>
      <div>했니?</div>
      <div>{list?.done}</div>
      <button onClick={() => modifyItem(e, list.id)}>수정!</button>
      <button onClick={() => deleteItem(list.id)}>삭제!</button>
    </div>
  );
};

export default TodoItem;
