import { url } from "../app";

const TodoInput = () => {
  const [content, setContent] = useState();

  const addTodo = () => {
    const shortid = require("shortid");
    const id = shortid.generate();
    const newTodo = {
      id: id,
      content: content,
      createdAt: new Date().toISOString()
    };
    axios.post(url + `/todo`);
  };
  return (
    <div>
      <input />
      <button onClick={addTodo}>+1</button>
    </div>
  );
};

export default TodoInput;
