import React, { useEffect, useState } from "react";
import axios from "axios";
import styled from "styled-components";

const TodoItem = ({ list, getData }) => {
  const [isModify, setIsModify] = useState(false);
  const [modifyText, setModifyText] = useState("");

  useEffect(() => {
    getData();
  }, []);

  const modifyItem = async (e) => {
    e.preventDefault();
    setIsModify(!isModify);
    await axios.put(`http://localhost:4001/todo/${list.id}`, {
      content: modifyText
    });
    getData();
  };

  const pressEnter = (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.nativeEvent.isComposing) {
      // isComposing 이 true 이면
      return; // 조합 중이므로 동작을 막는다.
    }

    if (e.key === "Enter" && e.shiftKey) {
      // [shift] + [Enter] 치면 걍 리턴
      return;
    } else if (e.key === "Enter") {
      // [Enter] 치면 메시지 보내기
      sendText(e);
    }
  };

  const modifyContent = (e) => {
    setModifyText(e.target.value);
  };

  const deleteItem = async (id) => {
    await axios.delete(`http://localhost:4001/todo/${id}`);
    getData();
  };

  return (
    <Item>
      <div>할 일</div>
      {isModify ? (
        <input name="text" onChange={modifyContent} />
      ) : (
        <div>{list?.content}</div>
      )}
      <div>날짜</div>
      <div>{list?.updatedAt}</div>
      <div>했니?</div>
      <div>{list?.done}</div>
      {isModify ? (
        <></>
      ) : (
        <>
          <input
            onKeyDown={pressEnter}
            onClick={modifyItem}
            type="submit"
            value="수정!"
          />
          <button onClick={() => deleteItem(list.id)}>삭제!</button>
        </>
      )}
    </Item>
  );
};

const Item = styled.div`
  display: flex;
`;
export default TodoItem;
